import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  carts: [],
};

const findsExistence = (cart) => {};

export const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    addCart: (state, action) => {
      const cart = {
        item: action.payload.item,
        img: action.payload.img,
        type: action.payload.type,
        quantity: action.payload.quantity,
        sellingPrice: action.payload.sellingPrice,
        deliveryTime: action.payload.deliveryTime,
        selectedQty: 1,
      };
      console.log(action.payload);
      if (state.carts.includes(cart)) {
        let idx = state.carts.findIndex(cart);
        state.carts[idx].selectedQty = state.carts[idx].selectedQty + 1;
      } else {
        state.carts.push(cart);
      }
    },
    removeCart: (state, action) => {
      const cart = {
        item: action.payload.item,
        img: action.payload.img,
        type: action.payload.type,
        quantity: action.payload.quantity,
        sellingPrice: action.payload.sellingPrice,
        deliveryTime: action.payload.deliveryTime,
        selectedQty: 0,
      };
      console.log(action.payload);
      if (findsExistence(cart)) {
        let idx = state.carts.findIndex(cart);
        state.carts[idx].selectedQty = state.carts[idx].selectedQty - 1;
      } else {
        state.carts.push(cart);
      }
    },
  },
});

export const { addCart, removeCart } = cartSlice.actions;

export default cartSlice.reducer;
